import { useState } from 'react';
import React from 'react';
import { useRef } from 'react';
import { FaPencilAlt, FaTimes } from 'react-icons/fa';

import "../index.css"
const Issue = ({ issue, onDelete, onEdit }) => {
    return (
      <div>
        <div className="issue">
          <div>
            <p className="issueName">
              <span className="textBold">issue Name:</span> {issue.text}
            </p>
        <p className="issueDate"><span className="textBold">Date of Completion:</span>              {issue.day}
            </p>
            </div>
            <div>
            <p><FaTimes onClick={() => onDelete(issue.id)} className="delIcon" /></p>
          <p><FaPencilAlt onClick={() => onEdit(issue.id)} className="editIcon" /></p>
          </div>
        </div>
      </div>
    )
}
export default Issue;
