import React from 'react';
import Webcam from "react-webcam";
import { useRef } from 'react';

const Camera = () => {
    
    const webRef=useRef(null);

    let img ="httpsL;';'";
    const showImage=()=>{
        img = webRef.current.getScreenshot();
    };

    return (
        <div>
            <h1>Camera</h1>
            <Webcam ref={webRef} />
            <button onClick={()=>{
                showImage()
            }}>Image in Console</button>
            <br />
        </div>
    );
}

export default Camera;
