import React from 'react';
import Issues from './components/Issues';
import AddIssue from './components/AddIssue';

const List = () => {
    return (
        <label><div className="container">
        {/* App Header */}
        <header showForm={() => setShowAddIssue(!showAddIssue)} changeTextAndColor={showAddIssue} />
        
        {/* Revealing the Add Issue Form */}
        {showAddIssue && <AddIssue onSave={addIssue} />}
        
        {/* Displaying Issues */}
        {
          issues.length > 0 ?
            (<Issues issues={issues} />) :
            ('No Issue Found!')
        }
      </div></label>
    );
}

export default List;
