import React from 'react';
import Webcam from "react-webcam";
import { useRef } from 'react';
import { useEffect } from 'react';

import 'bootstrap/dist/css/bootstrap.min.css';
import { CssBaseline, Grid } from '@material-ui/core';

const Camera = () => {

    let videoRef = useRef(null);
    let photoRef = useRef(null)
  
    const getVideo = () => {
      navigator.mediaDevices
        .getUserMedia({
          video: true
        })
        .then((stream) => {
          let video = videoRef.current;
          video.srcObject = stream;
          video.play();
        })
        .catch((err) => {
          console.error(err);
        });
    };
  
    const takePicture = () => {
      const width = 400
      const height = width / (16 / 9)
      
      let video = videoRef.current
      let photo = photoRef.current
  
      photo.width = width
      photo.height = height
  
      let ctx = photo.getContext('2d')
  
      ctx.drawImage(video, 0, 0, width, height)
      
    }
  
    const clearImage = () => {
      let photo = photoRef.current
      let ctx = photo.getContext('2d')
  
      ctx.clearRect(0,0,photo.width,photo.height)
    }
  
    useEffect(() => {
      getVideo();
    }, [videoRef]);
  
    return (
<Grid container spacing={2}>
        <Grid item xs={8}>
        <h1 className="text-center">Take Photo of Incident</h1>
        <canvas className="container" ref={photoRef}></canvas>
          <button onClick={clearImage} className="btn btn-primary container">Clear Image</button>
        </Grid>

        <Grid item xs={8}>
        <video ref={videoRef} className="container"></video>
        <button onClick={takePicture} className="btn btn-danger container">Take Picture</button>
        </Grid>
        </Grid>


    );
}

export default Camera;
